    private void enter_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enter_buttonActionPerformed
        user_verify.setUser(user_input.getText());
        user_verify.setPass(pass_input.getText());
        this.input.show();
        if (user_verify.log()){
            this.aceptado.setText("Usuario Aceptado! : D");
        }else {
            this.aceptado.setText("Usuario Rechazado! D :");
        }
    }//GEN-LAST:event_enter_buttonActionPerformed

